package jaxp.validate;import java.text.*;import org.xml.sax.*;import org.xml.sax.helpers.*;public class ErrorPrinter   extends DefaultHandler{   private MessageFormat message = new MessageFormat("({0}: {1}, {2}): {3}");         private void print(SAXParseException x)   {      String msg = message.format(new Object[]                                  {                                     x.getSystemId(),                                     new Integer(x.getLineNumber()),                                     new Integer(x.getColumnNumber()),                                     x.getMessage()                                  });      System.out.println(msg);   }         public void warning(SAXParseException x)   {      print(x);   }   public void error(SAXParseException x)   {      print(x);   }            public void fatalError(SAXParseException x)      throws SAXParseException   {      print(x);      throw x;   }}